/**
 * LanguageManager - รีเซ็ต scroll dropdown หลัง delay ก่อนลบ dropdown ออกจาก DOM (ผู้ใช้จะไม่เห็น dropdown รีเซ็ต)
 * ป้องกัน scroll jump, scroll ทะลุ, auto update config/language, dropdown UI สมบูรณ์
 * ปรับปรุงล่าสุด: 2025-05-17
 * @version 2.9.6
 *
 * หมายเหตุ: ให้ใส่ปุ่มไว้ใน HTML ตั้งแต่แรก เช่น
 * <div id="language-selector-container">
 *   <button id="language-button" class="default-lang-class" type="button">
 *     <!-- span.lang-btn-txt จะถูก JS เติมให้ทุกภาษา -->
 *   </button>
 * </div>
 */
class LanguageManager {
  constructor() {
    this.languagesConfig = {};
    this.selectedLang = '';
    this.lastSelectedLang = '';
    this.isLanguageDropdownOpen = false;
    this.languageCache = new Map();
    this.isUpdatingLanguage = false;
    this.isNavigating = false;
    this.mutationObserver = null;
    this.scrollPosition = 0;

    this.RETRY_ATTEMPTS = 3;
    this.RETRY_DELAY = 0;
    this.FADE_DURATION = 300;
    this.UPDATE_DELAY = 0;

    this.mutationThrottleTimeout = null;

    this.isInitialized = false;
    this.pendingTranslations = new Map();

    this.dbPromise = this.getIndexedDBVersionAndOpen();
  }

  getIndexedDBVersionAndOpen() {
    return new Promise((resolve, reject) => {
      const dbName = 'language_data_store';
      let db;
      const detectReq = indexedDB.open(dbName);
      let detectedVersion = 1;
      detectReq.onsuccess = () => {
        db = detectReq.result;
        detectedVersion = db.version || 1;
        const hasLang = db.objectStoreNames.contains('lang');
        const hasConfig = db.objectStoreNames.contains('config');
        db.close();
        if (hasLang && hasConfig) {
          const req = indexedDB.open(dbName, detectedVersion);
          req.onsuccess = () => resolve(req.result);
          req.onerror = () => reject(req.error);
          req.onblocked = () => {
            console.warn('IndexedDB request is blocked.');
          };
        } else {
          const upgradeReq = indexedDB.open(dbName, detectedVersion + 1);
          upgradeReq.onupgradeneeded = () => {
            const db = upgradeReq.result;
            if (!db.objectStoreNames.contains('lang')) {
              db.createObjectStore('lang', { keyPath: 'languageCode' });
            }
            if (!db.objectStoreNames.contains('config')) {
              db.createObjectStore('config', { keyPath: 'key' });
            }
          };
          upgradeReq.onsuccess = () => resolve(upgradeReq.result);
          upgradeReq.onerror = () => reject(upgradeReq.error);
          upgradeReq.onblocked = () => {
            console.warn('IndexedDB request is blocked.');
          };
        }
      };
      detectReq.onerror = () => {
        const req = indexedDB.open(dbName, 1);
        req.onupgradeneeded = () => {
          const db = req.result;
          if (!db.objectStoreNames.contains('lang')) {
            db.createObjectStore('lang', { keyPath: 'languageCode' });
          }
          if (!db.objectStoreNames.contains('config')) {
            db.createObjectStore('config', { keyPath: 'key' });
          }
        };
        req.onsuccess = () => resolve(req.result);
        req.onerror = () => reject(req.error);
        req.onblocked = () => {
          console.warn('IndexedDB request is blocked.');
        };
      };
    });
  }

  getConfigFromIndexedDB() {
    return this.dbPromise.then(db => {
      return new Promise((resolve, reject) => {
        const tx = db.transaction('config', 'readonly');
        const store = tx.objectStore('config');
        const req = store.get('languagesConfig');
        req.onsuccess = () => { resolve(req.result ? req.result.data : null); };
        req.onerror = () => reject(req.error);
      });
    });
  }

  storeConfigInIndexedDB(configData) {
    return this.dbPromise.then(db => {
      return new Promise((resolve, reject) => {
        const tx = db.transaction('config', 'readwrite');
        const store = tx.objectStore('config');
        const req = store.put({ key: 'languagesConfig', data: configData });
        req.onsuccess = () => resolve();
        req.onerror = () => reject(req.error);
      });
    });
  }

  getLangFromIndexedDB(languageCode) {
    return this.dbPromise.then(db => {
      return new Promise((resolve, reject) => {
        const tx = db.transaction('lang', 'readonly');
        const store = tx.objectStore('lang');
        const req = store.get(languageCode);
        req.onsuccess = () => { resolve(req.result ? req.result.data : null); };
        req.onerror = () => reject(req.error);
      });
    });
  }

  storeLangInIndexedDB(languageCode, data) {
    return this.dbPromise.then(db => {
      return new Promise((resolve, reject) => {
        const tx = db.transaction('lang', 'readwrite');
        const store = tx.objectStore('lang');
        const req = store.put({ languageCode, data });
        req.onsuccess = () => resolve();
        req.onerror = () => reject(req.error);
      });
    });
  }

  async getLatestLanguagesConfig() {
    let latestConfig = null;
    let fetchFailed = false;
    for (let attempt = 1; attempt <= this.RETRY_ATTEMPTS; attempt++) {
      try {
        const response = await fetch('/assets/json/language.min.json', { cache: 'no-store' });
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        latestConfig = await response.json();
        break;
      } catch (error) {
        if (attempt === this.RETRY_ATTEMPTS) {
          fetchFailed = true;
          console.error("ไม่สามารถโหลด languagesConfig ได้:", error);
        }
      }
    }
    return fetchFailed ? null : latestConfig;
  }

  compareConfigContent(obj1, obj2) {
    const k1 = Object.keys(obj1);
    const k2 = Object.keys(obj2);
    if (k1.length !== k2.length) return false;
    for (let lang of k1) {
      if (!(lang in obj2)) return false;
      if (JSON.stringify(obj1[lang]) !== JSON.stringify(obj2[lang])) return false;
    }
    return true;
  }

  async loadLanguagesConfig() {
    try {
      const storedConfig = await this.getStoredConfig();
      const latestConfig = await this.getLatestLanguagesConfig();

      let shouldUpdate = false;
      if (
        latestConfig &&
        (
          !storedConfig ||
          !(await this.validateConfig(storedConfig)) ||
          !this.compareConfigContent(storedConfig, latestConfig)
        )
      ) {
        this.languagesConfig = latestConfig;
        await this.storeConfigInIndexedDB(latestConfig);
        shouldUpdate = true;
      } else if (storedConfig && await this.validateConfig(storedConfig)) {
        this.languagesConfig = storedConfig;
      } else {
        throw new Error("Config ไม่ถูกต้องและโหลดใหม่ไม่ได้");
      }
      await this.prepareAllButtonTexts();
      await this.handleInitialLanguage();
      if (shouldUpdate) this.updateLanguageSelectorUI();
      else this.initializeCustomLanguageSelector();
    } catch (error) {
      console.error('Error loading language configuration:', error);
      this.showError('ไม่สามารถโหลดข้อมูลภาษาได้');
      this.fallbackToDefaultLanguage();
    }
  }

  // สร้าง span สำหรับแต่ละภาษาไว้ในปุ่มล่วงหน้า
  async prepareAllButtonTexts() {
    this.languageButton = document.getElementById('language-button');
    if (!this.languageButton) return;
    // ลบ span เดิมออกก่อน
    Array.from(this.languageButton.querySelectorAll('.lang-btn-txt')).forEach(e => e.remove());
    // สร้าง span สำหรับแต่ละภาษา
    Object.entries(this.languagesConfig).forEach(([lang, config]) => {
      const span = document.createElement('span');
      span.className = 'lang-btn-txt';
      span.dataset.lang = lang;
      span.textContent = config.buttonText || 'Language';
      span.style.display = 'none';
      this.languageButton.appendChild(span);
    });
    // แสดงเฉพาะภาษาที่เลือก
    this.showButtonTextForLang(this.selectedLang || 'en');
  }

  // เรียกเมื่อเปลี่ยนภาษา หรือหลังสร้าง/เตรียม span
  showButtonTextForLang(lang) {
    this.languageButton = document.getElementById('language-button');
    if (!this.languageButton) return;
    Array.from(this.languageButton.querySelectorAll('.lang-btn-txt')).forEach(span => {
      span.style.display = (span.dataset.lang === lang) ? '' : 'none';
    });
  }

  updateLanguageSelectorUI() {
    this.initializeCustomLanguageSelector();
  }

  async getStoredConfig() {
    try {
      const storedConfig = await this.getConfigFromIndexedDB();
      return storedConfig;
    } catch {
      return null;
    }
  }

  async validateConfig(config) {
    return config &&
      typeof config === 'object' &&
      Object.keys(config).length > 0 &&
      Object.values(config).every(lang =>
        lang.label &&
        lang.buttonText &&
        typeof lang.label === 'string' &&
        typeof lang.buttonText === 'string'
      );
  }

  async handleInitialLanguage() {
    this.storeOriginalContent();
    const urlParams = new URLSearchParams(window.location.search);
    const langFromUrl = urlParams.get('lang');
    const storedLang = localStorage.getItem('selectedLang') || 'en';
    this.selectedLang = storedLang;
    this.showButtonTextForLang(this.selectedLang); // เพิ่มความเร็วในการแสดงผล
    if (this.selectedLang !== 'en') {
      await this.updatePageLanguage(this.selectedLang, { updateHistory: false });
    }
  }

  determineInitialLanguage(langFromUrl) {
    if (langFromUrl && this.languagesConfig[langFromUrl]) return langFromUrl;
    const storedLang = localStorage.getItem('selectedLang');
    if (storedLang && this.languagesConfig[storedLang]) return storedLang;
    return this.detectBrowserLanguage();
  }

  detectBrowserLanguage() {
    const browserLanguages = navigator.languages || [navigator.language || navigator.userLanguage];
    return browserLanguages
      .map(lang => lang.split('-')[0])
      .find(lang => this.languagesConfig[lang]) || 'en';
  }

  async loadLanguageData(languageCode) {
    if (this.languageCache.has(languageCode)) {
      return this.languageCache.get(languageCode);
    }

    let localData = await this.getLangFromIndexedDB(languageCode);

    let fetchedData = null;
    let flattenedFetched = null;
    let fetchFailed = false;
    const jsonPath = `/assets/lang/${languageCode}.min.json`;

    for (let attempt = 1; attempt <= this.RETRY_ATTEMPTS; attempt++) {
      try {
        const response = await fetch(jsonPath, { cache: 'no-store' });
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        fetchedData = await response.json();
        flattenedFetched = this.flattenLanguageJson(fetchedData);
        break;
      } catch (error) {
        if (attempt === this.RETRY_ATTEMPTS) {
          fetchFailed = true;
          console.error(`ไม่สามารถโหลดภาษา ${languageCode} ได้:`, error);
          this.showError(`ไม่สามารถโหลดภาษา ${languageCode} ได้`);
        }
      }
    }

    if (fetchFailed) {
      if (localData) {
        this.languageCache.set(languageCode, localData);
        return localData;
      }
      return null;
    }

    let isSame = false;
    if (localData && typeof localData === 'object') {
      isSame = this.compareLanguageContent(localData, flattenedFetched);
    }

    if (!isSame) {
      await this.storeLangInIndexedDB(languageCode, flattenedFetched);
      this.languageCache.set(languageCode, flattenedFetched);
      return flattenedFetched;
    } else {
      this.languageCache.set(languageCode, localData);
      return localData;
    }
  }

  compareLanguageContent(obj1, obj2) {
    const keys1 = Object.keys(obj1);
    const keys2 = Object.keys(obj2);
    if (keys1.length !== keys2.length) return false;
    for (let k of keys1) {
      if (!(k in obj2)) return false;
      if (obj1[k] !== obj2[k]) return false;
    }
    return true;
  }

  flattenLanguageJson(json) {
    const result = {};
    function recur(obj) {
      for (const [k, v] of Object.entries(obj)) {
        if (typeof v === 'object' && v !== null) {
          recur(v);
        } else {
          result[k] = v;
        }
      }
    }
    recur(json);
    return result;
  }

  async initialize() {
    if (this.isInitialized) return;
    try {
      await this.loadLanguagesConfig();
      this.observeMutations();
      this.setupNavigationHandlers();
      document.documentElement.setAttribute("lang", "");
      this.isInitialized = true;
    } catch (error) {
      console.error('Error during initialization:', error);
      this.showError('ไม่สามารถเริ่มต้นระบบได้');
    }
  }

  async updatePageLanguage(language, options = { updateHistory: true }) {
    if (this.isUpdatingLanguage) return;
    try {
      this.isUpdatingLanguage = true;
      this.lastSelectedLang = this.selectedLang;
      document.documentElement.setAttribute("lang", "");
      document.documentElement.setAttribute("lang", language);
      if (language === this.detectBrowserLanguage()) {
        document.documentElement.setAttribute("translate", "no");
        if (!document.querySelector('meta[name="google"][content="notranslate"]')) {
          const meta = document.createElement('meta');
          meta.name = "google";
          meta.content = "notranslate";
          document.head.appendChild(meta);
        }
      } else {
        document.documentElement.removeAttribute("translate");
        const meta = document.querySelector('meta[name="google"][content="notranslate"]');
        if (meta) { meta.remove(); }
      }
      if (language === 'en') {
        await this.resetToEnglishContent();
      } else {
        const languageData = await this.loadLanguageData(language);
        if (languageData) {
          if (window.requestIdleCallback) {
            requestIdleCallback(() => this.translatePageContent(languageData));
          } else {
            await this.translatePageContent(languageData);
          }
        } else {
          await this.resetToEnglishContent();
        }
      }
      this.selectedLang = language;
      this.showButtonTextForLang(this.selectedLang); // อัปเดตทันที
      if (options.updateHistory) this.updateURLAndHistory(language);
      localStorage.setItem('selectedLang', language);
    } catch (error) {
      console.error('Error updating page language:', error);
      this.showError('เกิดข้อผิดพลาดในการเปลี่ยนภาษา');
      await this.resetToEnglishContent();
    } finally {
      this.isUpdatingLanguage = false;
    }
  }

  updateURLAndHistory(language) {
    const url = new URL(window.location.href);
    if (language === 'en') {
      url.searchParams.delete('lang');
    } else {
      url.searchParams.set('lang', language);
    }
    const newUrl = url.toString();
    if (newUrl === window.location.href) return;
    const state = {
      language: language,
      lastLanguage: this.lastSelectedLang,
      timestamp: Date.now()
    };
    history.replaceState(state, '', newUrl);
  }

  setupNavigationHandlers() {
    window.addEventListener('popstate', async (event) => {
      if (this.isLanguageDropdownOpen) await this.closeLanguageDropdown();
      if (this.isNavigating) return;
      this.isNavigating = true;
      try {
        const storedLang = localStorage.getItem('selectedLang') || 'en';
        if (storedLang !== this.selectedLang) {
          await this.updatePageLanguage(storedLang, { updateHistory: false });
        }
      } finally {
        this.isNavigating = false;
      }
    });
  }

  async translatePageContent(languageData) {
    const elements = document.querySelectorAll('[data-translate]');
    const aTagRegex = /@a(.*?)@/g;
    await Promise.all(
      Array.from(elements).map(async (el) => {
        const key = el.getAttribute('data-translate');
        if (!languageData[key]) return;
        let translation = languageData[key];
        translation = translation.replace(/@br/g, "<br>");
        translation = translation.replace(/@strong(.*?)@/g, "<strong>$1</strong>");
        const hasMedia = el.querySelector('img, svg');
        if (aTagRegex.test(translation)) {
          if (!hasMedia) {
            const parts = translation.split(/(@a.*?@)/);
            const anchorElements = Array.from(el.querySelectorAll('a'));
            let anchorIndex = 0;
            const newHTML = parts.map(part => {
              const markerMatch = part.match(/^@a(.*?)@$/);
              if (markerMatch) {
                const translatedLinkText = markerMatch[1];
                if (translatedLinkText.trim() === "") {
                  if (anchorIndex < anchorElements.length) {
                    const origAnchor = anchorElements[anchorIndex++];
                    let attrString = "";
                    Array.from(origAnchor.attributes).forEach(attr => {
                      attrString += ` ${attr.name}="${attr.value}"`;
                    });
                    return `<a${attrString}>${origAnchor.textContent}</a>`;
                  }
                  return "";
                }
                if (anchorIndex < anchorElements.length) {
                  const origAnchor = anchorElements[anchorIndex++];
                  let attrString = "";
                  Array.from(origAnchor.attributes).forEach(attr => {
                    attrString += ` ${attr.name}="${attr.value}"`;
                  });
                  return `<a${attrString}>${translatedLinkText}</a>`;
                } else {
                  return translatedLinkText;
                }
              } else {
                return part;
              }
            }).join('');
            el.innerHTML = newHTML;
          } else {
            let matches = [];
            let match;
            while ((match = aTagRegex.exec(translation)) !== null) {
              matches.push(match[1]);
            }
            const anchorElements = Array.from(el.querySelectorAll('a'));
            anchorElements.forEach((a, index) => {
              if (matches[index] && matches[index].trim() !== "") {
                a.textContent = matches[index];
              }
            });
            const plainTranslation = translation.replace(aTagRegex, '');
            this.replaceTextOnly(el, plainTranslation);
          }
        } else {
          if (!hasMedia) {
            el.innerHTML = translation;
          } else {
            this.replaceTextOnly(el, translation);
          }
        }
      })
    );
  }

  replaceTextOnly(element, newText) {
    element.childNodes.forEach((node) => {
      if (node.nodeType === Node.TEXT_NODE && node.textContent.trim()) {
        node.textContent = newText;
      } else if (node.nodeType === Node.ELEMENT_NODE && !['IMG', 'SVG'].includes(node.tagName)) {
        this.replaceTextOnly(node, newText);
      }
    });
  }

  initializeCustomLanguageSelector() {
    const container = document.getElementById('language-selector-container');
    this.languageButton = document.getElementById('language-button');
    if (!this.languageButton) return;
    this.prepareAllButtonTexts();
    this.showButtonTextForLang(this.selectedLang || 'en');

    if (this.languageOverlay && this.languageOverlay.parentElement) {
      this.languageOverlay.parentElement.removeChild(this.languageOverlay);
      this.languageOverlay = null;
    }
    if (this.languageDropdown && this.languageDropdown.parentElement) {
      this.languageDropdown.parentElement.removeChild(this.languageDropdown);
      this.languageDropdown = null;
    }

    this.languageOverlay = document.createElement('div');
    this.languageOverlay.id = 'language-overlay';
    document.body.appendChild(this.languageOverlay);

    this.languageDropdown = document.createElement('div');
    this.languageDropdown.id = 'language-dropdown';
    this.languageDropdown.innerHTML = '';
    this.populateLanguageDropdown();
    document.body.appendChild(this.languageDropdown);

    this.setupEventListeners();
    this.setupDropdownScrollLock();
  }

  populateLanguageDropdown() {
    const fragment = document.createDocumentFragment();
    Object.entries(this.languagesConfig).forEach(([lang, config]) => {
      const option = document.createElement('div');
      option.className = 'language-option';
      option.textContent = config.label;
      option.dataset.language = lang;
      fragment.appendChild(option);
    });
    this.languageDropdown.innerHTML = '';
    this.languageDropdown.appendChild(fragment);
  }

  setupEventListeners() {
    if (!this.languageButton) return;
    this.languageButton.onclick = () => this.toggleLanguageDropdown();
    this.languageOverlay.onclick = () => this.closeLanguageDropdown();
    this.languageDropdown.onclick = (e) => {
      const option = e.target.closest('.language-option');
      if (option) {
        const lang = option.dataset.language;
        if (lang) this.selectLanguage(lang);
      }
    };
  }

  // ป้องกัน scroll ทะลุเมื่อเลื่อน #language-dropdown
  setupDropdownScrollLock() {
    if (!this.languageDropdown) return;
    this.languageDropdown.removeEventListener('wheel', this._dropdownWheelListener, { passive: false });
    this.languageDropdown.removeEventListener('touchstart', this._dropdownTouchStartListener);
    this.languageDropdown.removeEventListener('touchmove', this._dropdownTouchMoveListener, { passive: false });

    this._dropdownWheelListener = (e) => {
      const el = this.languageDropdown;
      const delta = e.deltaY;
      const atTop = el.scrollTop === 0;
      const atBottom = el.scrollTop + el.clientHeight >= el.scrollHeight - 1;
      if ((atTop && delta < 0) || (atBottom && delta > 0)) {
        e.preventDefault();
      }
      e.stopPropagation();
    };
    this.languageDropdown.addEventListener('wheel', this._dropdownWheelListener, { passive: false });

    let lastY = 0;
    this._dropdownTouchStartListener = (e) => {
      if (e.touches.length === 1) lastY = e.touches[0].clientY;
    };
    this._dropdownTouchMoveListener = (e) => {
      if (e.touches.length !== 1) return;
      const el = this.languageDropdown;
      const currentY = e.touches[0].clientY;
      const delta = lastY - currentY;
      lastY = currentY;

      const atTop = el.scrollTop === 0;
      const atBottom = el.scrollTop + el.clientHeight >= el.scrollHeight - 1;

      if ((atTop && delta < 0) || (atBottom && delta > 0)) {
        e.preventDefault();
      }
      e.stopPropagation();
    };
    this.languageDropdown.addEventListener('touchstart', this._dropdownTouchStartListener);
    this.languageDropdown.addEventListener('touchmove', this._dropdownTouchMoveListener, { passive: false });
  }

  toggleLanguageDropdown() {
    this.isLanguageDropdownOpen ? this.closeLanguageDropdown() : this.openLanguageDropdown();
  }

  async openLanguageDropdown() {
    if (this.isLanguageDropdownOpen) return;
    this.scrollPosition = window.scrollY || window.pageYOffset || document.documentElement.scrollTop || 0;
    this.isLanguageDropdownOpen = true;
    this.languageOverlay.style.display = 'block';
    this.languageDropdown.style.display = 'block';

    document.body.style.position = 'fixed';
    document.body.style.left = '0';
    document.body.style.right = '0';
    document.body.style.overflowY = 'scroll';
    document.body.style.top = `-${this.scrollPosition}px`;
    document.body.classList.add('scroll-lock');

    return new Promise(resolve => {
      requestAnimationFrame(() => {
        this.languageOverlay.classList.add('fade-in');
        this.languageDropdown.classList.add('fade-in');
        setTimeout(resolve, this.FADE_DURATION);
      });
    });
  }

  async closeLanguageDropdown() {
    if (!this.isLanguageDropdownOpen) return;
    const dropdownResetDelay = 80;
    return new Promise(resolve => {
      this.isLanguageDropdownOpen = false;
      this.languageOverlay.classList.remove('fade-in');
      this.languageDropdown.classList.remove('fade-in');
      this.languageOverlay.classList.add('fade-out');
      this.languageDropdown.classList.add('fade-out');

      document.body.classList.remove('scroll-lock');
      document.body.style.position = '';
      document.body.style.left = '';
      document.body.style.right = '';
      document.body.style.overflowY = '';
      document.body.style.top = '';

      window.scrollTo(0, this.scrollPosition);

      setTimeout(() => {
        if (this.languageDropdown) {
          this.languageDropdown.scrollTop = 0;
          this.languageDropdown.scrollLeft = 0;
        }
      }, this.FADE_DURATION - dropdownResetDelay);

      setTimeout(() => {
        this.languageOverlay.style.display = 'none';
        this.languageDropdown.style.display = 'none';
        this.languageOverlay.classList.remove('fade-out');
        this.languageDropdown.classList.remove('fade-out');
        resolve();
      }, this.FADE_DURATION);
    });
  }

  async selectLanguage(language) {
    if (!this.languagesConfig[language]) {
      console.warn(`ไม่รองรับภาษา: ${language} กำลังใช้ภาษาอังกฤษแทน`);
      language = 'en';
    }
    if (this.selectedLang === language && !this.isNavigating) {
      await this.closeLanguageDropdown();
      return;
    }
    this.lastSelectedLang = this.selectedLang;
    await this.updatePageLanguage(language);
    await this.closeLanguageDropdown();
  }

  storeOriginalContent() {
    document.querySelectorAll('[data-translate]').forEach(el => {
      if (!el.hasAttribute('data-original-text')) {
        el.setAttribute('data-original-text', el.textContent.trim());
      }
      if (!el.hasAttribute('data-original-style')) {
        el.setAttribute('data-original-style', el.style.cssText);
      }
    });
  }

  async resetToEnglishContent() {
    const elements = document.querySelectorAll('[data-translate]');
    const resetPromises = Array.from(elements).map(el => new Promise(resolve => {
      requestAnimationFrame(() => {
        const originalText = el.getAttribute('data-original-text');
        const originalStyle = el.getAttribute('data-original-style');
        if (originalText) {
          this.replaceTextOnly(el, originalText);
        }
        if (originalStyle) {
          el.style.cssText = originalStyle;
        }
        resolve();
      });
    }));
    await Promise.all(resetPromises);
  }

  validateLanguageData(data) {
    return data &&
      typeof data === 'object' &&
      Object.keys(data).length > 0 &&
      Object.values(data).every(text =>
        typeof text === 'string' && text.trim().length > 0
      );
  }

  async fallbackToDefaultLanguage() {
    this.selectedLang = 'en';
    await this.resetToEnglishContent();
    this.showButtonTextForLang('en');
    this.updateURLAndHistory('en');
    localStorage.setItem('selectedLang', 'en');
  }

  showError(message) {
    const errorDiv = document.createElement('div');
    errorDiv.className = 'language-error';
    errorDiv.textContent = message;
    Object.assign(errorDiv.style, {
      position: 'fixed',
      top: '20px',
      right: '20px',
      backgroundColor: '#ff4444',
      color: 'white',
      padding: '10px 20px',
      borderRadius: '4px',
      zIndex: '9999',
      opacity: '0',
      transition: 'opacity 0.3s ease'
    });
    document.body.appendChild(errorDiv);
    requestAnimationFrame(() => {
      errorDiv.style.opacity = '1';
      setTimeout(() => {
        errorDiv.style.opacity = '0';
        setTimeout(() => errorDiv.remove(), this.FADE_DURATION);
      }, 3000);
    });
  }

  observeMutations() {
    if (this.mutationObserver) { this.mutationObserver.disconnect(); }
    this.mutationObserver = new MutationObserver((mutations) => {
      if (this.mutationThrottleTimeout) return;
      this.mutationThrottleTimeout = setTimeout(() => {
        let needsUpdate = false;
        mutations.forEach(mutation => {
          if (mutation.type === 'childList') {
            mutation.addedNodes.forEach(node => {
              if (node.nodeType === Node.ELEMENT_NODE) {
                const translatableElements = node.querySelectorAll('[data-translate]');
                if (translatableElements.length > 0) {
                  needsUpdate = true;
                  translatableElements.forEach(el => {
                    if (!el.hasAttribute('data-original-text')) {
                      el.setAttribute('data-original-text', el.textContent.trim());
                    }
                  });
                }
              }
            });
          }
        });
        if (needsUpdate && this.selectedLang !== 'en') {
          this.updatePageLanguage(this.selectedLang, { updateHistory: false });
        }
        this.mutationThrottleTimeout = null;
      }, 50);
    });
    this.mutationObserver.observe(document.body, { childList: true, subtree: true });
  }

  destroy() {
    if (this.languageOverlay) this.languageOverlay.remove();
    if (this.languageDropdown) this.languageDropdown.remove();
    if (this.mutationObserver) this.mutationObserver.disconnect();
    this.languageCache.clear();
    this.isInitialized = false;
    this.isLanguageDropdownOpen = false;
    this.isUpdatingLanguage = false;
    this.isNavigating = false;
  }
}

// สร้าง instance และเริ่มต้นการทำงาน
const languageManager = new LanguageManager();

window.addEventListener('DOMContentLoaded', () => {
  languageManager.initialize();
});

if (typeof module !== 'undefined' && module.exports) {
  module.exports = languageManager;
}